
import java.util.Scanner;


public class StarPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("This the programm to print star pattern.");
		
		int N = 5;
				
		int i,j;
		
		for (i=1;i<=5;i++)
		
		{
			for (j=1;j<=i; j++) 
			{
		
				System.out.print("*");
			}
			System.out.println(" ");
		}
	}
	


}
